import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Container, Row } from 'react-bootstrap';

const Trending = () => {
  const [trendingData, setTrendingData] = useState([]);

  useEffect(() => {
    fetchTrendingData();
  }, []);

  const fetchTrendingData = async () => {
    try {
      const response = await axios.get('https://seleksi-sea-2023.vercel.app/api/movies');
      setTrendingData(response.data);
    } catch (error) {
      console.error('Error fetching trending data:', error);
    }
  };

  return (
    <div>
        <Container>
            <Row>
                <Col className='moviePoster'>
                    {trendingData.map((item) => (
                        <div className="card text-dark text-center" key={item.id}>
                            <img className="card-img" src={item.poster_url} alt="Card image" />
                            <h5 className="card-title">{item.title}</h5>
                        </div>
                        // </div>
                    ))}
                </Col>
            </Row>
        </Container>
    </div>
  );
};

export default Trending;
